                lizi = new cc.ParticleFlower();
                break;
            case 4:
                lizi = new cc.ParticleGalaxy();
                break;
            case 5:
                lizi = new cc.ParticleMeteor();
                break;
            case 6:
                lizi = new cc.ParticleSpiral();
                break;
            case 7:
                lizi = new cc.ParticleSnow();
                break;
