        {
            case 11:
                allnpc.push(newobj);
                this.addChild(newobj);
                break;
            case 12:
                var oldobj = allnpc.pop();
                this.removeChild(oldobj);
                break;
        }
        this.getChildByTag(3).setString("数组共"+allnpc.length+"个Node");
    }
});

var HelloWorldScene = cc.Scene.extend({
    onEnter:function () {
        this._super();
        var layer = new HelloWorldLayer();
        this.addChild(layer);
    }
});
