```javascript
onLoad: function () {
    var self = this;
    this.node.on(cc.Node.EventType.TOUCH_START,function(event){
        if(!self.game.isCal){
            cc.audioEngine.playEffect(self.clickEffect);
            self.game.isCal = true;
